mapper.update:
UPDATE menu
MenuName = #{menuName}, menuPic = #{menuPic}, menuDescription = #{menuDescription}, menuPrice = #{menuPrice},
WHERE MenuId = #{menuId} AND MerchantId = #{merchantId}

mapper.delete:
DELETE FROM menu
WHERE MenuId IN

    #{id}

AND MerchantId = #{merchantId}

mapper.select:
SELECT * FROM menu

    
        AND MenuName LIKE CONCAT('%', #{menuName}, '%')
    
    
        AND MerchantId = #{merchantId}
